import React from 'react';
import axios from 'axios';

const TaskItem = ({ task, deleteTask, updateTask }) => {
  const handleDelete = () => {
    deleteTask(task.id);
  };

  const handleCompleteChange = async (e) => {
    const updatedTask = { ...task, completed: e.target.checked };
    const response = await axios.put(`${process.env.REACT_APP_API_URL}/tasks/${task.id}`, updatedTask);
    updateTask(response.data);
  };

  return (
    <li>
      <input
        type="checkbox"
        checked={task.completed}
        onChange={handleCompleteChange}
      />
      {task.description}
      <button onClick={handleDelete}>Delete</button>
    </li>
  );
};

export default TaskItem;
